import React, { useState} from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import { tipJar } from './HeroSection';
import './Navbar.css';

function Navbar() {
  const [click, setClick] = useState(false);
  // const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  // const showButton = () => {
  //   if (window.innerWidth <= 960) {
  //     setButton(false);
  //   } else {
  //     setButton(true);
  //   }
  // };

  // useEffect(() => {
  //   showButton();
  // }, []);

  // window.addEventListener('resize', showButton);
  const tipETH = async () => { 
    try {
      const { ethereum } = window;

      if (ethereum) {
        // const provider = new ethers.providers.Web3Provider(ethereum);
        // const signer = provider.getSigner();
        const value = 0.005 // ether
        window.open(`https://pay.buildship.dev/to/${tipJar}?value=${value}`,'payment','width=500, height=800');

        // const nftContract = new ethers.Contract(contractAddress, abi, signer);

        // console.log("Initialize payment");
        // let nftTxn = await nftContract.mintNFTs(1, { value: ethers.utils.parseEther("0.01") });

        // console.log("Mining... please wait");
        // await nftTxn.wait();

        // console.log("Mined, see transaction: https://rinkeby.etherscan.io/tx/${nftTxn.hash");

      } else {
        console.log("Ethereum object does not exist");
      }

    } catch (err) {
      console.log(err);
    }
  }
  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            {/* XOJUSTKIRST */}
            <a href="https://www.fontspace.com/category/cursive"><img src="https://see.fontimg.com/api/renderfont4/z89Pa/eyJyIjoiZnMiLCJoIjozMiwidyI6MTAwMCwiZnMiOjMyLCJmZ2MiOiIjMDAwMDAwIiwiYmdjIjoiI0ZGRkZGRiIsInQiOjF9/WE9KVVNUS0lSU1Q/amazing-mother.png" alt="Cursive fonts"/></a>
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <Button  
          className='btns'
            buttonStyle='btn--outline'
            buttonSize='btn--large'
            // onClick={mintNftHandler}
            onClick={tipETH}
            >
            Send Tip
          </Button>
          {/* <Link
              class='social-icon-link instagram'
              // to='/https://instagram.com/pandapennypicks'
              target='_blank'
              aria-label='Instagram'
            >
              <a href='https://instagram.com/xojustkirst'>
                <i class='fab fa-instagram' />
              </a>
          </Link>           */}
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/FAQ'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                FAQ
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/products'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Products
              </Link>
            </li>

            <li>
              {/* <Link
                to='/sign-up'
                className='nav-links-mobile'
                onClick={closeMobileMenu}
              >
                Sign Up
              </Link> */}
              <Link
                  class='social-icon-link instagram'
                  className='nav-links'
                  // to='/https://instagram.com/pandapennypicks'
                  target='_blank'
                  aria-label='Instagram'
                >
                  <a href='https://instagram.com/xojustkirst'>
                    <i class='fab fa-instagram' />
                  </a>
              </Link>   
            </li>
          </ul>
          {/* {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>} */}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
