import React from 'react';
import '../../App.css';
// import Cards from '../Cards';
import HeroSection from '../HeroSection';
import $ from 'jquery';
//var counter = 1; //instantiate a counter
//var maxImage = 9; //the total number of images that are available
function Home() {
  $().ready(function(){
    var i = 0;

  //   Background Images
    var images = [
      'https://picsum.photos/id/237/200/300',
      'https://picsum.photos/200/300?grayscale',
      'https://picsum.photos/200/300/?blur'
    ]

  //   pick body element ID
    var image = $('.home')

  //   set initial background-image
    image.css('background-image', 'url(https://picsum.photos/200/300/?blur)')

  //   change image every after 5 seconds
    setInterval(function(){
      image.fadeOut(1000, function(){
        image.css('background-image', 'url(' + images [i++] +')')
        image.fadeIn(1000)
      })
      if(1 == images.length)
        i = 0
    }, 5000)
  })
  return (
    <>
      <HeroSection />
      {/* <Cards /> */}
    </>
  );
}

export default Home;
